import React, { useState, useEffect } from 'react';
import '../styles/settings.css';

const Settings = () => {
  const [user, setUser] = useState(null);
  const [activeTab, setActiveTab] = useState('account');
  const [notifications, setNotifications] = useState({
    email: true,
    push: true,
    sms: false
  });

  useEffect(() => {
    const currentUser = localStorage.getItem('currentUser');
    if (currentUser) {
      setUser(JSON.parse(currentUser));
    }
  }, []);

  const handleNotificationChange = (type) => {
    setNotifications(prev => ({
      ...prev,
      [type]: !prev[type]
    }));
  };

  const handleSaveSettings = async () => {
    try {
      const response = await axios.put('http://localhost:8080/api/user/settings', {
        notifications,
        // Add other settings here
      });
      localStorage.setItem('currentUser', JSON.stringify(response.data));
      alert('Settings saved successfully!');
    } catch (error) {
      alert(error.response?.data?.message || 'Failed to save settings');
    }
  };

  if (!user) {
    return <div className="loading">Loading settings...</div>;
  }

  return (
    <div className="settings-container">
      <div className="settings-header">
        <h1>Settings</h1>
        <p>Manage your account preferences</p>
      </div>

      <div className="settings-content">
        <div className="settings-sidebar">
          <button 
            className={`sidebar-btn ${activeTab === 'account' ? 'active' : ''}`}
            onClick={() => setActiveTab('account')}
          >
            <i className="fas fa-user"></i>
            Account
          </button>
          <button 
            className={`sidebar-btn ${activeTab === 'privacy' ? 'active' : ''}`}
            onClick={() => setActiveTab('privacy')}
          >
            <i className="fas fa-shield-alt"></i>
            Privacy
          </button>
          <button 
            className={`sidebar-btn ${activeTab === 'notifications' ? 'active' : ''}`}
            onClick={() => setActiveTab('notifications')}
          >
            <i className="fas fa-bell"></i>
            Notifications
          </button>
          <button 
            className={`sidebar-btn ${activeTab === 'security' ? 'active' : ''}`}
            onClick={() => setActiveTab('security')}
          >
            <i className="fas fa-lock"></i>
            Security
          </button>
        </div>

        <div className="settings-main">
          {activeTab === 'account' && (
            <div className="settings-section">
              <h2>Account Information</h2>
              <div className="form-group">
                <label>Full Name</label>
                <input 
                  type="text" 
                  value={`${user.firstName} ${user.lastName}`}
                  readOnly
                />
              </div>
              <div className="form-group">
                <label>Email</label>
                <input type="email" value={user.email} readOnly />
              </div>
              <div className="form-group">
                <label>Username</label>
                <input type="text" value={user.username} readOnly />
              </div>
              <div className="form-group">
                <label>Department</label>
                <input type="text" value={user.department} readOnly />
              </div>
              <button className="save-btn" onClick={handleSaveSettings}>
                Save Changes
              </button>
            </div>
          )}

          {activeTab === 'privacy' && (
            <div className="settings-section">
              <h2>Privacy Settings</h2>
              <div className="privacy-option">
                <div className="option-info">
                  <h3>Profile Visibility</h3>
                  <p>Control who can see your profile</p>
                </div>
                <select className="privacy-select">
                  <option value="public">Public</option>
                  <option value="friends">Friends Only</option>
                  <option value="private">Private</option>
                </select>
              </div>
              <div className="privacy-option">
                <div className="option-info">
                  <h3>Post Visibility</h3>
                  <p>Default visibility for new posts</p>
                </div>
                <select className="privacy-select">
                  <option value="public">Public</option>
                  <option value="friends">Friends Only</option>
                  <option value="private">Private</option>
                </select>
              </div>
              <button className="save-btn" onClick={handleSaveSettings}>
                Save Changes
              </button>
            </div>
          )}

          {activeTab === 'notifications' && (
            <div className="settings-section">
              <h2>Notification Preferences</h2>
              <div className="notification-option">
                <div className="option-info">
                  <h3>Email Notifications</h3>
                  <p>Receive notifications via email</p>
                </div>
                <label className="toggle-switch">
                  <input 
                    type="checkbox" 
                    checked={notifications.email}
                    onChange={() => handleNotificationChange('email')}
                  />
                  <span className="slider"></span>
                </label>
              </div>
              <div className="notification-option">
                <div className="option-info">
                  <h3>Push Notifications</h3>
                  <p>Receive push notifications</p>
                </div>
                <label className="toggle-switch">
                  <input 
                    type="checkbox" 
                    checked={notifications.push}
                    onChange={() => handleNotificationChange('push')}
                  />
                  <span className="slider"></span>
                </label>
              </div>
              <div className="notification-option">
                <div className="option-info">
                  <h3>SMS Notifications</h3>
                  <p>Receive notifications via SMS</p>
                </div>
                <label className="toggle-switch">
                  <input 
                    type="checkbox" 
                    checked={notifications.sms}
                    onChange={() => handleNotificationChange('sms')}
                  />
                  <span className="slider"></span>
                </label>
              </div>
              <button className="save-btn" onClick={handleSaveSettings}>
                Save Changes
              </button>
            </div>
          )}

          {activeTab === 'security' && (
            <div className="settings-section">
              <h2>Security Settings</h2>
              <div className="security-option">
                <div className="option-info">
                  <h3>Two-Factor Authentication</h3>
                  <p>Add an extra layer of security to your account</p>
                </div>
                <button className="enable-btn">Enable</button>
              </div>
              <div className="security-option">
                <div className="option-info">
                  <h3>Change Password</h3>
                  <p>Update your account password</p>
                </div>
                <button className="change-btn">Change Password</button>
              </div>
              <div className="security-option">
                <div className="option-info">
                  <h3>Login Sessions</h3>
                  <p>Manage your active login sessions</p>
                </div>
                <button className="view-btn">View Sessions</button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Settings;
