import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNotification } from '../context/NotificationContext';
import '../styles/profile.css';

const Profile = () => {
  const { user, updateUser } = useAuth();
  const { showSuccess, showError } = useNotification();
  const [activeTab, setActiveTab] = useState('posts');
  const [selectedBackground, setSelectedBackground] = useState(user?.coverImage || 'https://via.placeholder.com/1200x300');
  const [selectedAvatar, setSelectedAvatar] = useState(user?.avatar || `https://via.placeholder.com/100x100/FFD700/000000?text=${user?.firstName?.charAt(0) || 'U'}`);
  const [showBackgroundModal, setShowBackgroundModal] = useState(false);
  const [showAvatarModal, setShowAvatarModal] = useState(false);

  const backgroundOptions = [
    'https://via.placeholder.com/1200x300/1a1a2e/FFD700?text=Daffy+Social',
    'https://via.placeholder.com/1200x300/16213e/FFD700?text=University+Life',
    'https://via.placeholder.com/1200x300/0f3460/FFD700?text=Student+Hub',
    'https://via.placeholder.com/1200x300/533483/FFD700?text=Academic+Excellence',
    'https://via.placeholder.com/1200x300/2d1b69/FFD700?text=Innovation+Center',
    'https://via.placeholder.com/1200x300/1a1a2e/FFD700?text=Future+Leaders'
  ];

  const avatarOptions = [
    `https://via.placeholder.com/100x100/FFD700/000000?text=${user?.firstName?.charAt(0) || 'U'}`,
    'https://via.placeholder.com/100x100/00A3FF/ffffff?text=U',
    'https://via.placeholder.com/100x100/FF6B6B/ffffff?text=U',
    'https://via.placeholder.com/100x100/4CAF50/ffffff?text=U',
    'https://via.placeholder.com/100x100/FF9800/ffffff?text=U',
    'https://via.placeholder.com/100x100/9C27B0/ffffff?text=U'
  ];

  const userPosts = [
    {
      id: 1,
      content: 'Just finished my latest project! Excited to share the results.',
      time: '2 hours ago',
      likes: 15,
      comments: 8
    },
    {
      id: 2,
      content: 'Great day at the university today. Learning new things every day!',
      time: '1 day ago',
      likes: 23,
      comments: 12
    }
  ];

  const userStats = {
    posts: 45,
    followers: 234,
    following: 156,
    likes: 1234
  };

  const handleBackgroundChange = (background) => {
    setSelectedBackground(background);
    const updatedUser = { ...user, coverImage: background };
    updateUser(updatedUser);
    setShowBackgroundModal(false);
    showSuccess('Background updated successfully!');
  };

  const handleAvatarChange = (avatar) => {
    setSelectedAvatar(avatar);
    const updatedUser = { ...user, avatar: avatar };
    updateUser(updatedUser);
    setShowAvatarModal(false);
    showSuccess('Profile picture updated successfully!');
  };

  if (!user) {
    return <div className="loading">Loading profile...</div>;
  }

  return (
    <div className="profile-container">
      {/* Background Modal */}
      {showBackgroundModal && (
        <div className="modal-overlay" onClick={() => setShowBackgroundModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Choose Background</h3>
              <button className="modal-close" onClick={() => setShowBackgroundModal(false)}>
                <i className="fas fa-times"></i>
              </button>
            </div>
            <div className="background-options">
              {backgroundOptions.map((bg, index) => (
                <div 
                  key={index} 
                  className="background-option"
                  onClick={() => handleBackgroundChange(bg)}
                >
                  <img src={bg} alt={`Background ${index + 1}`} />
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Avatar Modal */}
      {showAvatarModal && (
        <div className="modal-overlay" onClick={() => setShowAvatarModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Choose Profile Picture</h3>
              <button className="modal-close" onClick={() => setShowAvatarModal(false)}>
                <i className="fas fa-times"></i>
              </button>
            </div>
            <div className="avatar-options">
              {avatarOptions.map((avatar, index) => (
                <div 
                  key={index} 
                  className="avatar-option"
                  onClick={() => handleAvatarChange(avatar)}
                >
                  <img src={avatar} alt={`Avatar ${index + 1}`} />
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      <div className="profile-header">
        <div className="profile-cover">
          <img src={selectedBackground} alt="Cover" className="cover-image" />
          <button 
            className="edit-cover-btn"
            onClick={() => setShowBackgroundModal(true)}
          >
            <i className="fas fa-camera"></i>
            Change Cover
          </button>
        </div>
        <div className="profile-info">
          <div className="profile-avatar">
            <img src={selectedAvatar} alt="Profile" />
            <button 
              className="edit-avatar-btn"
              onClick={() => setShowAvatarModal(true)}
            >
              <i className="fas fa-camera"></i>
            </button>
          </div>
          <div className="profile-details">
            <h1 className="profile-name">{user.firstName} {user.lastName}</h1>
            <p className="profile-username">@{user.username}</p>
            <p className="profile-bio">Student at University â€¢ {user.department}</p>
            <div className="profile-stats">
              <div className="stat-item">
                <span className="stat-number">{userStats.posts}</span>
                <span className="stat-label">Posts</span>
              </div>
              <div className="stat-item">
                <span className="stat-number">{userStats.followers}</span>
                <span className="stat-label">Followers</span>
              </div>
              <div className="stat-item">
                <span className="stat-number">{userStats.following}</span>
                <span className="stat-label">Following</span>
              </div>
            </div>
            <button 
              className="edit-profile-btn" 
              onClick={() => window.location.href = '/settings'}
            >
              Edit Profile
            </button>
          </div>
        </div>
      </div>

      <div className="profile-content">
        <div className="profile-tabs">
          <button 
            className={`tab-btn ${activeTab === 'posts' ? 'active' : ''}`}
            onClick={() => setActiveTab('posts')}
          >
            <i className="fas fa-th"></i>
            Posts
          </button>
          <button 
            className={`tab-btn ${activeTab === 'about' ? 'active' : ''}`}
            onClick={() => setActiveTab('about')}
          >
            <i className="fas fa-user"></i>
            About
          </button>
          <button 
            className={`tab-btn ${activeTab === 'friends' ? 'active' : ''}`}
            onClick={() => setActiveTab('friends')}
          >
            <i className="fas fa-users"></i>
            Friends
          </button>
        </div>

        <div className="tab-content">
          {activeTab === 'posts' && (
            <div className="posts-tab">
              <div className="posts-grid">
                {userPosts.map((post) => (
                  <div key={post.id} className="profile-post">
                    <p className="post-content">{post.content}</p>
                    <div className="post-meta">
                      <span className="post-time">{post.time}</span>
                      <div className="post-stats">
                        <span><i className="fas fa-heart"></i> {post.likes}</span>
                        <span><i className="fas fa-comment"></i> {post.comments}</span>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {activeTab === 'about' && (
            <div className="about-tab">
              <div className="about-section">
                <h3>Personal Information</h3>
                <div className="info-grid">
                  <div className="info-item">
                    <span className="info-label">Full Name:</span>
                    <span className="info-value">{user.firstName} {user.lastName}</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">Email:</span>
                    <span className="info-value">{user.email}</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">Phone:</span>
                    <span className="info-value">{user.phone || 'Not provided'}</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">User Type:</span>
                    <span className="info-value">{user.userType}</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">Department:</span>
                    <span className="info-value">{user.department}</span>
                  </div>
                  {user.semester && (
                    <div className="info-item">
                      <span className="info-label">Semester:</span>
                      <span className="info-value">{user.semester}</span>
                    </div>
                  )}
                  {user.joiningYear && (
                    <div className="info-item">
                      <span className="info-label">Joining Year:</span>
                      <span className="info-value">{user.joiningYear}</span>
                    </div>
                  )}
                  {user.graduationYear && (
                    <div className="info-item">
                      <span className="info-label">Graduation Year:</span>
                      <span className="info-value">{user.graduationYear}</span>
                    </div>
                  )}
                </div>
              </div>
            </div>
          )}

          {activeTab === 'friends' && (
            <div className="friends-tab">
              <div className="friends-grid">
                <div className="friend-item">
                  <img src="https://via.placeholder.com/60x60" alt="Friend" />
                  <h4>John Doe</h4>
                  <p>Computer Science</p>
                </div>
                <div className="friend-item">
                  <img src="https://via.placeholder.com/60x60" alt="Friend" />
                  <h4>Jane Smith</h4>
                  <p>Electrical Engineering</p>
                </div>
                <div className="friend-item">
                  <img src="https://via.placeholder.com/60x60" alt="Friend" />
                  <h4>Mike Johnson</h4>
                  <p>Mechanical Engineering</p>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
