import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/auth.css'; // Auth CSS
import '@fortawesome/fontawesome-free/css/all.min.css'; // For icons

const Signup = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    username: '',
    password: '',
    confirmPassword: '',
    phone: '',
    userType: '',
    department: '',
    semester: '',
    joiningYear: '',
    graduationYear: '',
    terms: false,
    newsletter: false,
  });
  const [errors, setErrors] = useState({});
  const [passwordStrength, setPasswordStrength] = useState({ level: '', text: '' });
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  // Populate years dynamically (up to current year 2025)
  const currentYear = new Date().getFullYear();
  const years = [];
  for (let y = currentYear; y >= 1990; y--) {
    years.push(y);
  }

  // Handle input change
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
    if (name === 'password') checkPasswordStrength(value);
    validateField(name, value);
  };

  // Password strength check
  const checkPasswordStrength = (password) => {
    let strength = 0;
    if (password.length >= 8) strength += 1;
    if (password.length >= 12) strength += 1;
    if (/[a-z]/.test(password)) strength += 1;
    if (/[A-Z]/.test(password)) strength += 1;
    if (/[0-9]/.test(password)) strength += 1;
    if (/[^A-Za-z0-9]/.test(password)) strength += 1;

    let level = '', text = '';
    if (strength <= 2) { level = 'weak'; text = 'Weak'; }
    else if (strength <= 3) { level = 'fair'; text = 'Fair'; }
    else if (strength <= 4) { level = 'good'; text = 'Good'; }
    else { level = 'strong'; text = 'Strong'; }

    setPasswordStrength({ level, text });
  };

  // Field validation
  const validateField = (name, value) => {
    let error = '';
    if (!value && name !== 'phone' && name !== 'newsletter') error = `${name.charAt(0).toUpperCase() + name.slice(1)} is required`;
    if (name === 'email' && value && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value)) error = 'Invalid email';
    if (name === 'username' && value && value.length < 3) error = 'Username must be at least 3 characters';
    if (name === 'password' && value && value.length < 6) error = 'Password must be at least 6 characters';
    if (name === 'confirmPassword' && value !== formData.password) error = 'Passwords do not match';
    if (name === 'terms' && !formData.terms) error = 'You must agree to terms';

    setErrors((prev) => ({ ...prev, [name]: error }));
    return !error;
  };

  // Form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    let isValid = true;
    Object.keys(formData).forEach((key) => {
      if (!validateField(key, formData[key])) isValid = false;
    });

    if (isValid) {
      try {
        // Prepare payload (omit unnecessary fields based on userType)
        const payload = { ...formData };
        if (formData.userType !== 'student') delete payload.semester;
        if (formData.userType !== 'teacher') delete payload.joiningYear;
        if (formData.userType !== 'alumni') delete payload.graduationYear;
        delete payload.confirmPassword; // Don't send confirmPassword

        const response = await axios.post('http://localhost:8080/api/auth/signup', payload);
        alert('Signup successful! Check your email for verification.');
        navigate('/signin');
      } catch (error) {
        alert(error.response?.data?.message || 'Signup failed');
      }
    }
  };

  return (
    <div className="container">
      <div className="header">
        <div className="logo">
          <i className="fas fa-duck logo-icon"></i> {/* Assuming duck icon for Daffy */}
          <h1>Daffy</h1>
        </div>
        <h2>Create Account</h2>
        <p>Join Daffy to connect with friends and share your moments</p>
      </div>
      <form className="signup-form" onSubmit={handleSubmit}>
        <div className="form-row">
          <div className="form-group">
            <label>First Name</label>
            <input name="firstName" value={formData.firstName} onChange={handleChange} required />
            {errors.firstName && <span className="error-message">{errors.firstName}</span>}
          </div>
          <div className="form-group">
            <label>Last Name</label>
            <input name="lastName" value={formData.lastName} onChange={handleChange} required />
            {errors.lastName && <span className="error-message">{errors.lastName}</span>}
          </div>
        </div>
        <div className="form-group">
          <label>Email Address</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} required />
          {errors.email && <span className="error-message">{errors.email}</span>}
        </div>
        <div className="form-group">
          <label>Username</label>
          <input name="username" value={formData.username} onChange={handleChange} required />
          {errors.username && <span className="error-message">{errors.username}</span>}
        </div>
        <div className="form-group">
          <label>Password</label>
          <div className="input-wrapper">
            <input type={showPassword ? 'text' : 'password'} name="password" value={formData.password} onChange={handleChange} required />
            <button type="button" className="toggle-password" onClick={() => setShowPassword(!showPassword)}>
              <i className={`fas ${showPassword ? 'fa-eye-slash' : 'fa-eye'}`}></i>
            </button>
          </div>
          {errors.password && <span className="error-message">{errors.password}</span>}
          <div id="password-strength">
            <div id="strength-fill" className={`strength-fill ${passwordStrength.level}`}></div>
            <span id="strength-text">{passwordStrength.text ? `Password strength: ${passwordStrength.text}` : ''}</span>
          </div>
        </div>
        <div className="form-group">
          <label>Confirm Password</label>
          <div className="input-wrapper">
            <input type={showConfirmPassword ? 'text' : 'password'} name="confirmPassword" value={formData.confirmPassword} onChange={handleChange} required />
            <button type="button" className="toggle-password" onClick={() => setShowConfirmPassword(!showConfirmPassword)}>
              <i className={`fas ${showConfirmPassword ? 'fa-eye-slash' : 'fa-eye'}`}></i>
            </button>
          </div>
          {errors.confirmPassword && <span className="error-message">{errors.confirmPassword}</span>}
        </div>
        <div className="form-group">
          <label>Phone Number</label>
          <input name="phone" value={formData.phone} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>User Type</label>
          <select name="userType" value={formData.userType} onChange={handleChange} required>
            <option value="">Select User Type</option>
            <option value="student">Student</option>
            <option value="teacher">Teacher</option>
            <option value="alumni">Alumni</option>
          </select>
          {errors.userType && <span className="error-message">{errors.userType}</span>}
        </div>
        <div className="form-group">
          <label>Department</label>
          <select name="department" value={formData.department} onChange={handleChange} required>
            <option value="">Select Department</option>
            {/* Add all departments from your HTML */}
            <option value="Computer Science & Engineering">Computer Science & Engineering</option>
            {/* ... add others ... */}
            <option value="Veterinary Medicine">Veterinary Medicine</option>
          </select>
          {errors.department && <span className="error-message">{errors.department}</span>}
        </div>
        {formData.userType === 'student' && (
          <div className="form-group" id="semesterGroup">
            <label>Semester</label>
            <select name="semester" value={formData.semester} onChange={handleChange} required>
              <option value="">Select Semester</option>
              <option value="1st Semester">1st Semester</option>
              {/* ... add up to 12th ... */}
            </select>
            {errors.semester && <span className="error-message">{errors.semester}</span>}
          </div>
        )}
        {formData.userType === 'teacher' && (
          <div className="form-group" id="joiningYearGroup">
            <label>Joining Year</label>
            <select name="joiningYear" value={formData.joiningYear} onChange={handleChange} required>
              <option value="">Select Joining Year</option>
              {years.map((year) => <option key={year} value={year}>{year}</option>)}
            </select>
            {errors.joiningYear && <span className="error-message">{errors.joiningYear}</span>}
          </div>
        )}
        {formData.userType === 'alumni' && (
          <div className="form-group" id="graduationYearGroup">
            <label>Graduation Year</label>
            <select name="graduationYear" value={formData.graduationYear} onChange={handleChange} required>
              <option value="">Select Graduation Year</option>
              {years.map((year) => <option key={year} value={year}>{year}</option>)}
            </select>
            {errors.graduationYear && <span className="error-message">{errors.graduationYear}</span>}
          </div>
        )}
        <div className="form-group checkbox-group">
          <label className="checkbox-label">
            <input type="checkbox" name="terms" checked={formData.terms} onChange={handleChange} required />
            <span className="checkmark"></span>
            I agree to the Terms of Service and Privacy Policy
          </label>
          {errors.terms && <span className="error-message">{errors.terms}</span>}
        </div>
        <div className="form-group checkbox-group">
          <label className="checkbox-label">
            <input type="checkbox" name="newsletter" checked={formData.newsletter} onChange={handleChange} />
            <span className="checkmark"></span>
            Subscribe to newsletter and updates
          </label>
        </div>
        <button type="submit" className="create-btn">Create Account</button>
      </form>
      <div className="signin-link">
        Already have an account? <Link to="/signin">Sign In</Link>
      </div>
    </div>
  );
};

export default Signup;
