import React, { useState, useEffect } from 'react';
import '../styles/home.css';

const Home = () => {
  const [user, setUser] = useState(null);
  const [postText, setPostText] = useState('');
  const [posts, setPosts] = useState([
    {
      id: 1,
      user: { name: 'John Doe', avatar: 'https://via.placeholder.com/40x40' },
      content: 'Just finished an amazing project! Can\'t wait to share more details with everyone. #coding #webdev #excited',
      image: 'https://via.placeholder.com/600x400',
      time: '2 hours ago',
      likes: 15,
      comments: 8
    },
    {
      id: 2,
      user: { name: 'Jane Smith', avatar: 'https://via.placeholder.com/40x40' },
      content: 'Beautiful sunset today! Nature never fails to amaze me. ðŸŒ…',
      time: '5 hours ago',
      likes: 23,
      comments: 12
    }
  ]);

  useEffect(() => {
    const currentUser = localStorage.getItem('currentUser');
    if (currentUser) {
      setUser(JSON.parse(currentUser));
    }
  }, []);

  const handleCreatePost = () => {
    if (!postText.trim()) return;

    const newPost = {
      id: Date.now(),
      user: { 
        name: `${user?.firstName} ${user?.lastName}`, 
        avatar: `https://via.placeholder.com/40x40/FFD700/000000?text=${user?.firstName?.charAt(0) || 'U'}` 
      },
      content: postText,
      time: 'Just now',
      likes: 0,
      comments: 0
    };

    setPosts([newPost, ...posts]);
    setPostText('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleCreatePost();
    }
  };

  const openPostModal = (type) => {
    const typeNames = {
      'photo': 'Photo',
      'video': 'Video',
      'feeling': 'Feeling',
      'share': 'Share'
    };
    alert(`${typeNames[type]} post feature coming soon!`);
  };

  const createStory = () => {
    alert('Story creation feature coming soon!');
  };

  const openStory = (index) => {
    const storyNames = ['Your Story', 'John Doe', 'Jane Smith', 'Mike Johnson', 'Sarah Wilson', 'Alex Brown'];
    const storyName = storyNames[index] || 'Unknown User';
    alert(`Opening ${storyName}'s story...`);
  };

  return (
    <div className="main-container">
      <div className="main-content">
        {/* Create Post Section */}
        <div className="create-post-section">
          <div className="post-input-container">
            <img 
              src={`https://via.placeholder.com/40x40/FFD700/000000?text=${user?.firstName?.charAt(0) || 'U'}`}
              alt="User Avatar" 
              className="user-avatar-small"
            />
            <div className="post-input-wrapper">
              <input 
                type="text" 
                className="post-input" 
                placeholder="What's on your mind?"
                value={postText}
                onChange={(e) => setPostText(e.target.value)}
                onKeyPress={handleKeyPress}
              />
              <div className="post-actions">
                <button className="post-action-btn" onClick={() => openPostModal('photo')}>
                  <i className="fas fa-image"></i>
                  <span>Photo</span>
                </button>
                <button className="post-action-btn" onClick={() => openPostModal('video')}>
                  <i className="fas fa-video"></i>
                  <span>Video</span>
                </button>
                <button className="post-action-btn" onClick={() => openPostModal('feeling')}>
                  <i className="fas fa-smile"></i>
                  <span>Feeling</span>
                </button>
                <button className="post-action-btn" onClick={() => openPostModal('share')}>
                  <i className="fas fa-share"></i>
                  <span>Share</span>
                </button>
              </div>
              <button className="post-btn" onClick={handleCreatePost}>
                <i className="fas fa-paper-plane"></i>
                Share
              </button>
            </div>
          </div>
        </div>

        {/* Stories Section */}
        <div className="stories-section">
          <div className="stories-container">
            <div className="story-item" onClick={createStory}>
              <div className="story-avatar">
                <img src="https://via.placeholder.com/60x60" alt="Your Story" className="story-img" />
                <div className="story-add-icon">
                  <i className="fas fa-plus"></i>
                </div>
              </div>
              <span className="story-username">Your Story</span>
            </div>
            {['John Doe', 'Jane Smith', 'Mike Johnson', 'Sarah Wilson', 'Alex Brown'].map((name, index) => (
              <div key={index} className="story-item" onClick={() => openStory(index + 1)}>
                <div className="story-avatar has-story">
                  <img src="https://via.placeholder.com/60x60" alt={name} className="story-img" />
                </div>
                <span className="story-username">{name}</span>
              </div>
            ))}
          </div>
        </div>

        {/* Posts Feed */}
        <div className="posts-feed">
          {posts.map((post) => (
            <div key={post.id} className="post-card">
              <div className="post-header">
                <img src={post.user.avatar} alt="User" className="post-user-avatar" />
                <div className="post-user-info">
                  <h4 className="post-user-name">{post.user.name}</h4>
                  <span className="post-time">{post.time}</span>
                </div>
                <button className="post-menu-btn">
                  <i className="fas fa-ellipsis-h"></i>
                </button>
              </div>
              <div className="post-content">
                <p className="post-text">{post.content}</p>
                {post.image && (
                  <img src={post.image} alt="Post Image" className="post-image" />
                )}
              </div>
              <div className="post-actions">
                <button className="post-action">
                  <i className="fas fa-heart"></i>
                  <span>Like</span>
                </button>
                <button className="post-action">
                  <i className="fas fa-comment"></i>
                  <span>Comment</span>
                </button>
                <button className="post-action">
                  <i className="fas fa-share"></i>
                  <span>Share</span>
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
