import React, { useState } from 'react';
import '../styles/clubs.css';

const Clubs = () => {
  const [selectedCategory, setSelectedCategory] = useState('all');

  const categories = [
    { id: 'all', name: 'All Clubs' },
    { id: 'academic', name: 'Academic' },
    { id: 'sports', name: 'Sports' },
    { id: 'cultural', name: 'Cultural' },
    { id: 'technical', name: 'Technical' }
  ];

  const clubs = [
    {
      id: 1,
      name: 'Programming Club',
      category: 'technical',
      members: 156,
      description: 'Learn and practice programming with fellow enthusiasts',
      image: 'https://via.placeholder.com/300x200',
      isJoined: true
    },
    {
      id: 2,
      name: 'Photography Club',
      category: 'cultural',
      members: 89,
      description: 'Capture moments and share your photography skills',
      image: 'https://via.placeholder.com/300x200',
      isJoined: false
    },
    {
      id: 3,
      name: 'Football Club',
      category: 'sports',
      members: 234,
      description: 'Join us for exciting football matches and tournaments',
      image: 'https://via.placeholder.com/300x200',
      isJoined: false
    },
    {
      id: 4,
      name: 'Debate Club',
      category: 'academic',
      members: 67,
      description: 'Develop your public speaking and debating skills',
      image: 'https://via.placeholder.com/300x200',
      isJoined: true
    }
  ];

  const filteredClubs = selectedCategory === 'all' 
    ? clubs 
    : clubs.filter(club => club.category === selectedCategory);

  const handleJoinClub = (clubId) => {
    // Handle join/leave club logic
    alert('Club join/leave functionality coming soon!');
  };

  return (
    <div className="clubs-container">
      <div className="clubs-header">
        <h1>Clubs & Organizations</h1>
        <p>Join clubs that match your interests</p>
      </div>

      <div className="clubs-content">
        <div className="categories-filter">
          {categories.map((category) => (
            <button
              key={category.id}
              className={`category-btn ${selectedCategory === category.id ? 'active' : ''}`}
              onClick={() => setSelectedCategory(category.id)}
            >
              {category.name}
            </button>
          ))}
        </div>

        <div className="clubs-grid">
          {filteredClubs.map((club) => (
            <div key={club.id} className="club-card">
              <div className="club-image">
                <img src={club.image} alt={club.name} />
                <div className="club-category">{club.category}</div>
              </div>
              <div className="club-info">
                <h3 className="club-name">{club.name}</h3>
                <p className="club-description">{club.description}</p>
                <div className="club-stats">
                  <span className="members-count">
                    <i className="fas fa-users"></i>
                    {club.members} members
                  </span>
                </div>
                <button 
                  className={`join-btn ${club.isJoined ? 'joined' : ''}`}
                  onClick={() => handleJoinClub(club.id)}
                >
                  {club.isJoined ? 'Joined' : 'Join Club'}
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Clubs;
