import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNotification } from '../context/NotificationContext';
import '../styles/clubs.css';

const Clubs = () => {
  const { user } = useAuth();
  const { showSuccess, showError } = useNotification();
  const [clubs, setClubs] = useState([]);
  const [myClubs, setMyClubs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showNotifications, setShowNotifications] = useState(false);
  const [clubNotifications, setClubNotifications] = useState([]);
  const [createForm, setCreateForm] = useState({
    name: '',
    category: '',
    description: '',
    rules: '',
    email: ''
  });

  useEffect(() => {
    // This will be replaced with actual API calls when backend is ready
    loadClubs();
    loadClubNotifications();
  }, []);

  const loadClubs = async () => {
    try {
      setLoading(true);
      // TODO: Replace with actual API call
      // const response = await axios.get('/api/clubs');
      // setClubs(response.data.clubs);
      // setMyClubs(response.data.myClubs);
      
      // For now, show empty state
      setClubs([]);
      setMyClubs([]);
    } catch (error) {
      showError('Failed to load clubs');
    } finally {
      setLoading(false);
    }
  };

  const loadClubNotifications = async () => {
    try {
      // TODO: Replace with actual API call
      // const response = await axios.get('/api/clubs/notifications');
      // setClubNotifications(response.data.notifications);
      
      // For now, show empty state
      setClubNotifications([]);
    } catch (error) {
      showError('Failed to load club notifications');
    }
  };

  const handleJoinClub = async (clubId) => {
    try {
      // TODO: Replace with actual API call
      // await axios.post(`/api/clubs/${clubId}/join`);
      showSuccess('Successfully joined the club!');
      loadClubs(); // Reload data
    } catch (error) {
      showError('Failed to join club');
    }
  };

  const handleLeaveClub = async (clubId) => {
    try {
      // TODO: Replace with actual API call
      // await axios.post(`/api/clubs/${clubId}/leave`);
      showSuccess('Successfully left the club');
      loadClubs(); // Reload data
    } catch (error) {
      showError('Failed to leave club');
    }
  };

  const handleCreateClub = async (e) => {
    e.preventDefault();
    try {
      // TODO: Replace with actual API call
      // await axios.post('/api/clubs', createForm);
      showSuccess('Club created successfully!');
      setShowCreateModal(false);
      setCreateForm({
        name: '',
        category: '',
        description: '',
        rules: '',
        email: ''
      });
      loadClubs(); // Reload data
    } catch (error) {
      showError('Failed to create club');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCreateForm(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const filteredClubs = clubs.filter(club =>
    club.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    club.description.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const getBadgeIcon = (role) => {
    switch (role) {
      case 'president':
        return 'fas fa-crown';
      case 'vice_president':
        return 'fas fa-star';
      case 'executive':
        return 'fas fa-medal';
      case 'paid_member':
        return 'fas fa-gem';
      case 'unpaid_member':
        return 'fas fa-user';
      default:
        return 'fas fa-user';
    }
  };

  const getBadgeColor = (role) => {
    switch (role) {
      case 'president':
        return '#FFD700'; // Gold
      case 'vice_president':
        return '#C0C0C0'; // Silver
      case 'executive':
        return '#CD7F32'; // Bronze
      case 'paid_member':
        return '#00CED1'; // Cyan
      case 'unpaid_member':
        return '#808080'; // Gray
      default:
        return '#808080';
    }
  };

  if (loading) {
    return (
      <div className="clubs-container">
        <div className="loading">Loading clubs...</div>
      </div>
    );
  }

  return (
    <div className="clubs-container">
      {/* Fixed Top Bar */}
      <div className="clubs-top-bar">
        <div className="search-section">
          <div className="search-container">
            <i className="fas fa-search search-icon"></i>
            <input
              type="text"
              className="club-search-input"
              placeholder="Search clubs..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>
        </div>
        
        <div className="top-actions">
          <div className="notification-section">
            <button 
              className="notification-btn"
              onClick={() => setShowNotifications(!showNotifications)}
            >
              <i className="fas fa-bell"></i>
              {clubNotifications.length > 0 && (
                <span className="notification-badge">{clubNotifications.length}</span>
              )}
            </button>
            
            {showNotifications && (
              <div className="notification-dropdown">
                <h3>Club Notifications</h3>
                {clubNotifications.length === 0 ? (
                  <p>No new notifications</p>
                ) : (
                  clubNotifications.map((notification) => (
                    <div key={notification.id} className="notification-item">
                      <p>{notification.message}</p>
                      <span className="notification-time">{notification.time}</span>
                    </div>
                  ))
                )}
              </div>
            )}
          </div>
          
          <button 
            className="create-club-btn"
            onClick={() => setShowCreateModal(true)}
          >
            <i className="fas fa-plus"></i>
            Create Club
          </button>
        </div>
      </div>

      <div className="clubs-content">
        {/* My Clubs Section */}
        <div className="clubs-section">
          <h2>My Clubs ({myClubs.length})</h2>
          {myClubs.length === 0 ? (
            <div className="empty-clubs">
              <i className="fas fa-users"></i>
              <h3>You haven't joined any clubs yet</h3>
              <p>Discover and join clubs to connect with others</p>
            </div>
          ) : (
            <div className="clubs-grid">
              {myClubs.map((club) => (
                <div key={club.id} className="club-card my-club">
                  <div className="club-header">
                    <img src={club.avatar} alt={club.name} className="club-avatar" />
                    <div className="club-info">
                      <h3>{club.name}</h3>
                      <p>{club.description}</p>
                      <span className="club-members">{club.members} members</span>
                    </div>
                    <div className="user-badge">
                      <i 
                        className={getBadgeIcon(club.userRole)} 
                        style={{ color: getBadgeColor(club.userRole) }}
                      ></i>
                      <span className="badge-label">{club.userRole.replace('_', ' ')}</span>
                    </div>
                  </div>
                  <div className="club-actions">
                    <button className="btn-view-club">
                      <i className="fas fa-eye"></i>
                      View Club
                    </button>
                    <button 
                      className="btn-leave-club"
                      onClick={() => handleLeaveClub(club.id)}
                    >
                      <i className="fas fa-sign-out-alt"></i>
                      Leave
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Discover Clubs Section */}
        <div className="clubs-section">
          <h2>Discover Clubs ({filteredClubs.length})</h2>
          {filteredClubs.length === 0 ? (
            <div className="empty-clubs">
              <i className="fas fa-search"></i>
              <h3>No clubs found</h3>
              <p>Try adjusting your search or check back later</p>
            </div>
          ) : (
            <div className="clubs-grid">
              {filteredClubs.map((club) => (
                <div key={club.id} className="club-card">
                  <div className="club-header">
                    <img src={club.avatar} alt={club.name} className="club-avatar" />
                    <div className="club-info">
                      <h3>{club.name}</h3>
                      <p>{club.description}</p>
                      <span className="club-members">{club.members} members</span>
                    </div>
                  </div>
                  <div className="club-actions">
                    <button 
                      className="btn-join-club"
                      onClick={() => handleJoinClub(club.id)}
                    >
                      <i className="fas fa-user-plus"></i>
                      Join Club
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Create Club Modal */}
      {showCreateModal && (
        <div className="modal-overlay" onClick={() => setShowCreateModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2>Create New Club</h2>
              <button 
                className="modal-close"
                onClick={() => setShowCreateModal(false)}
              >
                <i className="fas fa-times"></i>
              </button>
            </div>
            
            <form onSubmit={handleCreateClub} className="create-club-form">
              <div className="form-group">
                <label>Name *</label>
                <input
                  type="text"
                  name="name"
                  value={createForm.name}
                  onChange={handleInputChange}
                  className="form-input"
                  required
                />
              </div>
              
              <div className="form-group">
                <label>Category *</label>
                <select
                  name="category"
                  value={createForm.category}
                  onChange={handleInputChange}
                  className="form-input"
                  required
                >
                  <option value="">Select Category</option>
                  <option value="academic">Academic</option>
                  <option value="sports">Sports</option>
                  <option value="cultural">Cultural</option>
                  <option value="technical">Technical</option>
                  <option value="social">Social</option>
                  <option value="other">Other</option>
                </select>
              </div>
              
              <div className="form-group">
                <label>Description *</label>
                <textarea
                  name="description"
                  value={createForm.description}
                  onChange={handleInputChange}
                  className="form-textarea"
                  rows="3"
                  required
                />
              </div>
              
              <div className="form-group">
                <label>Club Rules</label>
                <textarea
                  name="rules"
                  value={createForm.rules}
                  onChange={handleInputChange}
                  className="form-textarea"
                  rows="4"
                  placeholder="Enter club rules and guidelines..."
                />
              </div>
              
              <div className="form-group">
                <label>University Email *</label>
                <input
                  type="email"
                  name="email"
                  value={createForm.email}
                  onChange={handleInputChange}
                  className="form-input"
                  placeholder="your.email@university.edu"
                  required
                />
                <small>Verify your university club email</small>
              </div>
              
              <div className="form-actions">
                <button type="button" className="btn-cancel" onClick={() => setShowCreateModal(false)}>
                  Cancel
                </button>
                <button type="submit" className="btn-create">
                  Create Club
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Clubs;
