import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNotification } from '../context/NotificationContext';
import '../styles/profile.css';

const Profile = () => {
  const { user } = useAuth();
  const { showSuccess, showError } = useNotification();
  const [isEditing, setIsEditing] = useState(false);
  const [editingSection, setEditingSection] = useState(null);
  const [selectedBackground, setSelectedBackground] = useState(null);
  const [selectedAvatar, setSelectedAvatar] = useState(null);
  const [profileData, setProfileData] = useState({
    firstName: user?.firstName || 'Rokibul Islam',
    lastName: user?.lastName || 'Hamim',
    headline: 'Java backend developer | Bug Hunter | Programmer | Video editor',
    location: 'Gazipur, Dhaka, Bangladesh',
    email: 'hamim.pentester@gmail.com',
    about: '',
    experience: [],
    education: [],
    skills: [],
    contactInfo: {
      phone: '',
      website: '',
      linkedin: '',
      github: ''
    }
  });

  const [tempData, setTempData] = useState({});

  const handleBackgroundUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 10 * 1024 * 1024) {
        showError('Background image size should be less than 10MB');
        return;
      }
      setSelectedBackground(file);
      showSuccess('Background updated successfully!');
    }
  };

  const handleAvatarUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 5 * 1024 * 1024) {
        showError('Profile picture size should be less than 5MB');
        return;
      }
      setSelectedAvatar(file);
      showSuccess('Profile picture updated successfully!');
    }
  };

  const handleSaveProfile = async () => {
    try {
      showSuccess('Profile updated successfully!');
      setIsEditing(false);
    } catch (error) {
      showError('Failed to update profile');
    }
  };

  const handleEditSection = (section) => {
    setEditingSection(section);
    setTempData(profileData[section] || []);
  };

  const handleSaveSection = (section) => {
    setProfileData({ ...profileData, [section]: tempData });
    setEditingSection(null);
    setTempData({});
    showSuccess(`${section.charAt(0).toUpperCase() + section.slice(1)} updated successfully!`);
  };

  const handleAddItem = (section, newItem) => {
    const currentItems = profileData[section] || [];
    setProfileData({ ...profileData, [section]: [...currentItems, newItem] });
    showSuccess(`New ${section.slice(0, -1)} added successfully!`);
  };

  const handleDeleteItem = (section, index) => {
    const currentItems = profileData[section] || [];
    const updatedItems = currentItems.filter((_, i) => i !== index);
    setProfileData({ ...profileData, [section]: updatedItems });
    showSuccess(`${section.slice(0, -1)} deleted successfully!`);
  };

  return (
    <div className="profile-container">
      {/* Background Cover */}
      <div className="profile-cover">
        <div className="cover-image">
          {selectedBackground ? (
            <img 
              src={URL.createObjectURL(selectedBackground)} 
              alt="Cover" 
              className="cover-img"
            />
          ) : (
            <div className="default-cover">
              {/* Matrix-style background with geometric patterns */}
              <div className="matrix-bg">
                <div className="geometric-patterns">
                  <div className="circuit-lines"></div>
                  <div className="network-dots"></div>
                </div>
                <div className="circuit-border"></div>
              </div>
            </div>
          )}
          
          {/* Cover Info */}
          <div className="cover-info">
            <div className="cover-title">JR. PENTESTER</div>
            <div className="cover-handle">
              <i className="fas fa-fox"></i>
              XSecHamim
            </div>
            <div className="cover-email">
              <i className="fas fa-envelope"></i>
              {profileData.email}
            </div>
          </div>

          {/* Combined Cover Options Button */}
          <div className="cover-options">
            <div className="cover-dropdown">
              <button className="cover-option-btn">
                <i className="fas fa-camera"></i>
                <span>Cover</span>
                <i className="fas fa-chevron-down"></i>
              </button>
              <div className="cover-dropdown-menu">
                <label className="cover-dropdown-item">
                  <i className="fas fa-upload"></i>
                  <span>Change Cover</span>
                  <input 
                    type="file" 
                    accept="image/*" 
                    onChange={handleBackgroundUpload}
                    style={{ display: 'none' }}
                  />
                </label>
                <button 
                  className="cover-dropdown-item"
                  onClick={() => {
                    setSelectedBackground(null);
                    showSuccess('Cover photo removed successfully!');
                  }}
                >
                  <i className="fas fa-trash"></i>
                  <span>Delete Cover</span>
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Profile Avatar */}
        <div className="profile-avatar-section">
          <div className="profile-avatar">
            {selectedAvatar ? (
              <img 
                src={URL.createObjectURL(selectedAvatar)} 
                alt="Profile" 
                className="avatar-img"
              />
            ) : (
              <div className="default-avatar">
                <div className="arabic-text">قُلْ هُوَ اللَّهُ أَحَدٌ</div>
                <div className="hand-icon">☝️</div>
                <div className="arabic-text-bottom">بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ</div>
              </div>
            )}
            
            {/* Profile Icon Badge */}
            <div className="profile-icon-badge">
              <i className="fas fa-user"></i>
            </div>
          </div>
          
          {/* Edit Avatar Button */}
          <label className="edit-avatar-btn">
            <i className="fas fa-camera"></i>
            <input 
              type="file" 
              accept="image/*" 
              onChange={handleAvatarUpload}
              style={{ display: 'none' }}
            />
          </label>
        </div>
      </div>

      {/* Profile Details */}
      <div className="profile-details">
        <div className="profile-main-info">
          <div className="profile-name-section">
            <h1 className="profile-name">
              {profileData.firstName} {profileData.lastName}
            </h1>
          </div>
          
          <div className="profile-headline">{profileData.headline}</div>
          <div className="profile-location">
            <i className="fas fa-map-marker-alt"></i>
            {profileData.location}
            <span className="contact-link">Contact info</span>
          </div>
        </div>

        <div className="profile-actions">
          <button className="btn-edit-profile" onClick={() => setIsEditing(true)}>
            <i className="fas fa-pencil-alt"></i>
            Edit Profile
          </button>
        </div>
      </div>

      {/* Profile Content */}
      <div className="profile-content-grid">
        {/* Left Column - Main Content */}
        <div className="profile-left-column">
          {/* About Section */}
          <div className="profile-section">
            <div className="section-header">
              <h3>About</h3>
              <button className="edit-section-btn" onClick={() => handleEditSection('about')}>
                <i className="fas fa-pencil-alt"></i>
              </button>
            </div>
            <div className="section-content">
              {profileData.about ? (
                <p>{profileData.about}</p>
              ) : (
                <div className="no-content">
                  <p>No information to show</p>
                  <button className="add-section-btn" onClick={() => handleEditSection('about')}>
                    <i className="fas fa-plus"></i>
                    Add about
                  </button>
                </div>
              )}
            </div>
          </div>

          {/* Activity Section */}
          <div className="profile-section">
            <div className="section-header">
              <h3>Activity</h3>
            </div>
            <div className="section-content">
              <div className="upload-area">
                <div className="upload-placeholder">
                  <i className="fas fa-image"></i>
                  <p>Share a photo or video</p>
                </div>
                <div className="post-input-area">
                  <textarea 
                    placeholder="What's on your mind?" 
                    className="post-input"
                  />
                  <button className="post-btn">
                    <i className="fas fa-paper-plane"></i>
                    Post
                  </button>
                </div>
              </div>
            </div>
          </div>

          {/* Experience Section */}
          <div className="profile-section">
            <div className="section-header">
              <h3>Experience</h3>
              <button className="edit-section-btn" onClick={() => handleEditSection('experience')}>
                <i className="fas fa-plus"></i>
              </button>
            </div>
            <div className="section-content">
              {profileData.experience.length > 0 ? (
                profileData.experience.map((exp, index) => (
                  <div key={index} className="experience-item">
                    <div className="experience-header">
                      <h4>{exp.title}</h4>
                      <button 
                        className="delete-item-btn"
                        onClick={() => handleDeleteItem('experience', index)}
                      >
                        <i className="fas fa-trash"></i>
                      </button>
                    </div>
                    <p>{exp.company}</p>
                    <span>{exp.duration}</span>
                  </div>
                ))
              ) : (
                <div className="no-content">
                  <p>No experience to show</p>
                  <button className="add-section-btn" onClick={() => handleEditSection('experience')}>
                    <i className="fas fa-plus"></i>
                    Add experience
                  </button>
                </div>
              )}
            </div>
          </div>

          {/* Education Section */}
          <div className="profile-section">
            <div className="section-header">
              <h3>Education</h3>
              <button className="edit-section-btn" onClick={() => handleEditSection('education')}>
                <i className="fas fa-plus"></i>
              </button>
            </div>
            <div className="section-content">
              {profileData.education.length > 0 ? (
                profileData.education.map((edu, index) => (
                  <div key={index} className="education-item">
                    <div className="education-header">
                      <h4>{edu.degree}</h4>
                      <button 
                        className="delete-item-btn"
                        onClick={() => handleDeleteItem('education', index)}
                      >
                        <i className="fas fa-trash"></i>
                      </button>
                    </div>
                    <p>{edu.school}</p>
                    <span>{edu.year}</span>
                  </div>
                ))
              ) : (
                <div className="no-content">
                  <p>No education to show</p>
                  <button className="add-section-btn" onClick={() => handleEditSection('education')}>
                    <i className="fas fa-plus"></i>
                    Add education
                  </button>
                </div>
              )}
            </div>
          </div>

          {/* Skills Section */}
          <div className="profile-section">
            <div className="section-header">
              <h3>Skills</h3>
              <button className="edit-section-btn" onClick={() => handleEditSection('skills')}>
                <i className="fas fa-plus"></i>
              </button>
            </div>
            <div className="section-content">
              {profileData.skills.length > 0 ? (
                <div className="skills-list">
                  {profileData.skills.map((skill, index) => (
                    <span key={index} className="skill-tag">
                      {skill}
                      <button 
                        className="remove-skill-btn"
                        onClick={() => handleDeleteItem('skills', index)}
                      >
                        ×
                      </button>
                    </span>
                  ))}
                </div>
              ) : (
                <div className="no-content">
                  <p>No skills to show</p>
                  <button className="add-section-btn" onClick={() => handleEditSection('skills')}>
                    <i className="fas fa-plus"></i>
                    Add skills
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Right Column - Sidebar */}
        <div className="profile-right-column">
          {/* Contact Info Section */}
          <div className="profile-section">
            <div className="section-header">
              <h3>Contact Info</h3>
              <button className="edit-section-btn" onClick={() => handleEditSection('contactInfo')}>
                <i className="fas fa-pencil-alt"></i>
              </button>
            </div>
            <div className="section-content">
              {profileData.contactInfo.phone && (
                <div className="contact-item">
                  <i className="fas fa-phone"></i>
                  <span>{profileData.contactInfo.phone}</span>
                </div>
              )}
              {profileData.contactInfo.website && (
                <div className="contact-item">
                  <i className="fas fa-globe"></i>
                  <span>{profileData.contactInfo.website}</span>
                </div>
              )}
              {profileData.contactInfo.linkedin && (
                <div className="contact-item">
                  <i className="fab fa-linkedin"></i>
                  <span>{profileData.contactInfo.linkedin}</span>
                </div>
              )}
              {profileData.contactInfo.github && (
                <div className="contact-item">
                  <i className="fab fa-github"></i>
                  <span>{profileData.contactInfo.github}</span>
                </div>
              )}
              {!profileData.contactInfo.phone && !profileData.contactInfo.website && 
               !profileData.contactInfo.linkedin && !profileData.contactInfo.github && (
                <div className="no-content">
                  <p>No contact information</p>
                  <button className="add-section-btn" onClick={() => handleEditSection('contactInfo')}>
                    <i className="fas fa-plus"></i>
                    Add contact info
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
